import React, { useState, useEffect } from 'react';
import axios from "axios"
import "./Style.css"

const App = () => {
    // 백엔드 서버 기본 주소
    const serverURL = "http://127.0.0.1:5001/";
    //console.log(`${serverURL}/delete/${a}`);

    // 기본 메모 데이터
    const [memo, SetMemo] = useState([]);

    // 추가 input 데이터
    const [inputMemo, SetInputMemo] = useState("");

    // 초기에 서버로 부터 데이터 가져오기
    useEffect(() => {
        axios.get(serverURL)
        .then((response) => {
            const data = response.data;
            SetMemo(data);
            console.log("초기화 성공");
        })
    }, []);

    const GetMemo = () => {
        axios.get(serverURL)
        .then((response) => {
            const data = response.data;
            SetMemo(data);
            console.log("메모 가져오기 성공");
        });
    };

    const AddMemo = (new_content) => {
        if (new_content.trim() !== '' && new_content !== null){
            axios.post(`${serverURL}/add/${new_content}`)
        .then((response) => {
            console.log(response.data);
            SetInputMemo("");
            GetMemo();
        });
        }
    };

    const EditMemo = () => {
        const edit_content = 0;
        axios.put(`${serverURL}/update/${edit_content}`)
        .then(() => {

        });
    };

    const DeleteMemo = (id) => {
        const deleteUrl = `${serverURL}/delete/${id}`;
        axios.delete(deleteUrl)
        .then((response) => {
            console.log(response.data);
            GetMemo();
        });
    };

    const DataBox = (props) => {
        const [isEditmode, setEditmode] = useState(false);

        return (isEditmode ? 
            <div className="content_box--wrapper change">
                <input className="change_content" type="text" value={props.item.name}/>
                <button className="change_btn" onClick={() => {
                    console.log(1);
                }}>변경</button>
            </div>
            : 
            <div className="content_box--wrapper" key={props.item.id}>
                <div className="content_box">{props.item.name}</div>
                <button className="edit_btn" onClick={
                    () => {
                    setEditmode((current => !current));
                    }
                }><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                <button className="delete_btn" onClick={() => {DeleteMemo(props.item.id);
                }}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
            </div>
            );

    }

/*====== 테스트용 코드들 ======*/
    const Test1 = (e) => {
        const current_input = e.target.value;
        SetInputMemo(current_input);
    };

    const [test, SetTest] = useState("1");

    const Test2 = () => {
        console.log(test); 
        SetTest("2");
        prompt('Edit note:')
    };

    const Test3 = (e) => {
        console.log(1);
    };

    const data = [
        {id: 1, name: 'a'},
        {id: 2, name: 'b'},
    ];

    return (
        <div className="wrapper">
            <div className="content--wrapper">
                <div id="input--area" className="input--wrapper">
                    <input className="input_box" type="text" size="30" placeholder="내용 입력" onChange={(e) => {const current_input = e.target.value;
                    SetInputMemo(current_input);}} value={inputMemo}/>
                    <button className="add_btn" onClick={() => AddMemo(inputMemo)}>추가</button>
                    <button className="add_btn" onClick={() => {console.log(inputMemo);}}>테스트용</button>
                </div>
                <div id="content--area" className="content_area--wrapper">
                    <div className="content_box--wrapper">
                        <div className="content_box">1</div>
                        <button className="edit_btn" onClick={Test1}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                        <button className="delete_btn" onClick={Test2}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                    </div>
                    <div className="content_box--wrapper change">
                        <input className="change_content" type="text" size="30" placeholder="변경 내용 입력"/>
                        <button className="change_btn">변경</button>
                    </div>
                    {data.map((item) => {
                        return (
                            <div className="content_box--wrapper" key={item.id}>
                                <div className="content_box">{item.name}</div>
                                <button className="edit_btn" onClick={DeleteMemo}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                                <button className="delete_btn" onClick={Test3}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                            </div>
                        );
                    })}
                    {memo.map((item) => {
                        return (
                            <div className="content_box--wrapper" key={item.id}>
                                <div className="content_box">{item.content}</div>
                                <button className="edit_btn" onClick={Test1}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                                <button className="delete_btn" onClick={() => {DeleteMemo(item.id);
                                }}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                            </div>
                        );
                    })}
                    {data.map((item) => {
                        return (<DataBox key={item.id} item={item}/>);
                    })}
                </div>
        </div>
    </div>
    );
}

export default App;
