import React, { useState, useEffect } from 'react';
import axios from "axios"
import "./Style.css"

const App = () => {
    // 백엔드 서버 기본 주소
    const serverURL = "http://127.0.0.1:5001/";
    //console.log(`${serverURL}/delete/${a}`);
    console.log(2);


    const [memo, SetMemo] = useState([]);

    // 서버로 부터 데이터 가져오기
    useEffect(() => {
        axios.get(serverURL)
        .then((response) => {
            const data = response.data;
            SetMemo(data);
            console.log(2);
        })
    }, []);

    const GetMemo = () => {
        axios.get(serverURL)
        .then((response) => {
            const data = response.data;
            SetMemo(data);
            console.log(2);
        });
    };

    const AddMemo = () => {
        const new_content = "Test"
        axios.post(`${serverURL}/add/${new_content}`)
        .then((response) => {
            console.log(response);
        });
    };

    const EditMemo = () => {
        axios.put()
    };

    const DeleteMemo = (id) => {
        const deleteUrl = `${serverURL}/delete/${id}`;
        console.log(deleteUrl);
        axios.delete(deleteUrl)
        .then((response) => {
            console.log(response);
            const data = response.data;
            SetMemo(data);
        });
    };

    const Test1 = () => {

    };



    const Test2 = () => {
        console.log(SetMemo);
    };

    const data = [
        {id: 1, name: 'a'},
        {id: 2, name: 'b'},
        {id: 3, name: 'c'},
    ];

/*
<div className="content_box--wrapper change">
    <input className="change_content" type="text" value="변경 내용 입력" />
    <button className="change_btn">변경</button>
</div>
*/
    return (
        <div className="wrapper">
            <div className="content--wrapper">
                <div id="input--area" className="input--wrapper">
                    <input className="input_box" type="text" size="30" placeholder="내용 입력" />
                    <button className="add_btn" onClick={AddMemo}>추가</button>
                </div>
                <div id="content--area" className="content_area--wrapper">
                    <div className="content_box--wrapper">
                        <div className="content_box">1</div>
                        <button className="edit_btn" onClick={Test1}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                        <button className="delete_btn" onClick={Test2}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                    </div>
                    {data.map((item) => {
                        return (
                            <div className="content_box--wrapper" key={item.id}>
                                <div className="content_box">{item.name}</div>
                                <button className="edit_btn" onClick={DeleteMemo}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                                <button className="delete_btn" onClick={Test2}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                            </div>
                        );
                    })}
                    {memo.map((item) => {
                        return (
                            <div className="content_box--wrapper" key={item.id}>
                                <div className="content_box">{item.content}</div>
                                <button className="edit_btn" onClick={Test1}><img className="edit_btn_image" src="img/edit_btn.svg"/></button>
                                <button className="delete_btn" onClick={() => {
                                    DeleteMemo(item.id);
                                }}><img className="delete_btn_image" src="img/delete_btn.svg"/></button>
                            </div>
                        );
                    })}
                </div>
        </div>
    </div>
    );
}

export default App;
