    const Deletememo = () => {
        const id = 3;
        const deleteUrl = `${serverURL}/delete/${id}`;
        console.log(deleteUrl);
        axios.get(deleteUrl)
        .then((response) => {
            console.log(response.data);
            const data = response.data;
            Set_intial(data);
        })
    };

    const Test1 = () => {

