import React, { useState } from 'react';

function Notepad() {
  const [notes, setNotes] = useState([]);
  const [currentNote, setCurrentNote] = useState({ id: null, content: '' });

  const addNote = () => {
    if (currentNote.content.trim() !== '') {
      const newNote = { id: Date.now(), content: currentNote.content };
      setNotes([...notes, newNote]);
      setCurrentNote({ id: null, content: '' });
    }
  };

  const editNote = (id, content) => {
    const updatedNotes = notes.map(note => (note.id === id ? { id, content } : note));
    setNotes(updatedNotes);
    setCurrentNote({ id: null, content: '' });
  };

  const deleteNote = id => {
    const updatedNotes = notes.filter(note => note.id !== id);
    setNotes(updatedNotes);
  };

  return (
    <div>
      <h1>Notepad</h1>
      <ul>
        {notes.map(note => (
          <li key={note.id}>
            {note.content}
            <button onClick={() => editNote(note.id, prompt('Edit note:', note.content))}>
              Edit
            </button>
            <button onClick={() => deleteNote(note.id)}>
              Delete
            </button>
          </li>
        ))}
      </ul>
      <input
        type="text"
        value={currentNote.content}
        onChange={event => setCurrentNote({ id: currentNote.id, content: event.target.value })}
      />
      <button onClick={addNote}>
        Add note
      </button>
    </div>
  );
}

export default Notepad;
