  return (
    <div>
      <div>
				{data.map((item, idx, array) => {
					// 배열의 각 반복 요소, 인덱스 번호, 배열 전체
					return <Test key={idx} item={item} />;
				})}
			</div>
			<StyledButton
				onClick={() => {
					// setData(data + 1); // 비동기라서 위는 부정확하다
					setActive((current) => !current); // 정확하다
				}}
				active={isActive}
			>
				증가
